CREATE file.var
{ON ERROR statement(s)}
{THEN statement(s)}
{ELSE statement(s)}

CREATE.FILE path {DIRECTORY | DYNAMIC}
{GROUP.SIZE grpsz}
{BIG.REC.SIZE bigrec}
{MIN.MODULUS minmod}
{SPLIT.LOAD split}
{MERGE.LOAD merge}
{VERSION ver}
{ON ERROR statement(s)}

DEFFUN name {(arg1 {, arg2 ...})} {CALLING "func" | expr} {VAR.ARGS} {KEY key}
DEFFUN name {(arg1 {, arg2 ...})} LOCAL
DEFFUN name {(arg1 {, arg2 ...})} EXTERNAL {CALLING "libname"}
DEFFUN name {(arg1 {, arg2 ...})} EXTERNAL {CALLING "libname:funcname"}

DEL dyn.array<field {, value {, subvalue}}>
DELETE(dyn.array, field {, value {, subvalue}})
DEL collection{path}

DELETESEQ filename, id {NO.MAP} {ON ERROR statement(s)} {THEN statement(s)} {ELSE statement(s)}
or
DELETESEQ pathname {NO.MAP} {ON ERROR statement(s)} {THEN statement(s)} {ELSE statement(s)}

EXECUTE expr {TRAPPING ABORTS}
{CAPTURING var}
{NO.TTY}
{PASSLIST {src.list }}
{RTNLIST tgt.list }
{SILENT}
{STACKLIST}
{SETTING status.var} or {RETURNING status.var}

FOR var = start.expr TO limit.expr {STEP step.expr} {WHILE expr} {UNTIL expr} ...statement(s)... NEXT {var}
FOR var = value1, value2, value3... {WHILE expr} {UNTIL expr} ...statement(s)... NEXT {var}
FOR EACH var IN string {DELIMITER delim} {WHILE expr} {UNTIL expr} ...statement(s)... NEXT {var}

HUSH OFF {SETTING var}
HUSH ON {SETTING var}
HUSH expr {SETTING var}

LOCATE string IN dyn.array<field {,value {, subvalue}}> {BY order} {SETTING var} {THEN statement(s)} {ELSE statement(s)}
LOCATE string IN dyn.array{<field {,value }>} {, start}  {BY order} SETTING var {THEN statement(s)} {ELSE statement(s)}
LOCATE(string, dyn.array{, field {, value {, start }}}; var {; order}) {THEN statement(s)} {ELSE statement(s)}

MOUSE ON {SETTING var}
MOUSE OFF {SETTING var}
MOUSE expr {SETTING var}

PERFORM expr {TRAPPING ABORTS}
{CAPTURING var}
{NO.TTY}
{PASSLIST {src.list }}
{RTNLIST tgt.list }
{SILENT}
{STACKLIST}
{SETTING status.var} or {RETURNING status.var}

RETURN {expr}
RETURN TO label{:}
RETURN FROM PROGRAM

SELECT var {TO list.no} {ON ERROR statement(s)}
SELECTN var {TO list.no} {ON ERROR statement(s)}
SELECTV var {TO list.var} {ON ERROR statement(s)}

TRY {statement}
{statements}
CATCH exception {qualifiers}{, exception {qualifiers}...}
{statements}
CATCH exception {qualifiers}{, exception {qualifiers}...}
{statements}
END
